mapper.select:
select * from class c join loc_info l
using (loc_code)
join field_info f
using (field_code)
order by c.class_code desc

select * from class c join loc_info l
using (loc_code)
join field_info f
using (field_code)
where c.city like '%' || #{search} || '%' or f.field_name like '%' || #{search} || '%' or c.class_name like '%' || #{search} || '%' or c.captain like '%' || #{search} || '%'
